const reducer = (accumulator, currentValue) => (+accumulator) + (+currentValue);

module.exports.summ = (...number) => {
    const summNumbers = number.reduce(reducer);
    if (summNumbers) {
        let plusString = '';
        number.forEach((item, index) => {
            if (index !== number.length - 1) {
                plusString = plusString + item + '+'
            } else {
                plusString = plusString + item
            }
        });
        console.log(`${plusString} = ${summNumbers} `);
    } else {
        console.log(`Ви вели не коректні дані`);
    }

};